class Solution:
    # @param {string} s
    # @return {string}
    def longestPalindrome(self, s):
        ptr_set = set()
        if not s:
            return None
        if len(set(s)) == 1:
            return s
        longest_len = 1
        longest_string = s[0]
        for idx, char in enumerate(s):
            if idx == 0:
                continue
            new_ptr_set = set([])
            for ptr in ptr_set|{idx-1, idx-2}:
                if idx == 0 or (idx == 1 and ptr == -1):
                    continue
                if ptr == -1 and idx != 1:
                    if idx > longest_len:
                        longest_len = idx
                        longest_string = s[:idx]
                else:
                    if char != s[ptr]:
                        if idx - ptr - 1 > longest_len:
                            longest_len = idx - ptr - 1
                            longest_string = s[ptr+1:idx]
                    else:
                         new_ptr_set.add(ptr - 1)
            ptr_set = new_ptr_set
        if ptr_set:
            if len(s) - min(ptr_set) > longest_len:
                longest_string = s[min(ptr_set)+1:]
        return longest_string

